import React, { useEffect, useMemo, useState } from 'react';
import axios from 'axios';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';
const REFRESH_OPTIONS = [10000, 30000, 60000, 120000];

const formatNumber = (value, fractionDigits = 2) =>
  Number(value || 0).toLocaleString(undefined, {
    minimumFractionDigits: fractionDigits,
    maximumFractionDigits: fractionDigits
  });

const formatTimestamp = (value) =>
  value ? new Date(value).toLocaleString() : '—';

function App() {
  const [trades, setTrades] = useState([]);
  const [analysis, setAnalysis] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [apiStatus, setApiStatus] = useState('unknown');
  const [lastUpdated, setLastUpdated] = useState(null);
  const [responseTimeMs, setResponseTimeMs] = useState(null);
  const [autoRefreshMs, setAutoRefreshMs] = useState(30000);
  const [errorLog, setErrorLog] = useState([]);
  const [tradeFormError, setTradeFormError] = useState('');
  const [analysisFormError, setAnalysisFormError] = useState('');
  const [alertLog, setAlertLog] = useState([]);
  const [riskSettings, setRiskSettings] = useState(() => {
    const stored = localStorage.getItem('riskSettings');
    return stored
      ? JSON.parse(stored)
      : {
          maxDailyLoss: 500,
          maxExposure: 10000,
          stopLossPct: 1.5,
          takeProfitPct: 3.0
        };
  });

  const [tradeFilters, setTradeFilters] = useState({
    symbol: '',
    action: '',
    status: '',
    sort: 'timestamp_desc'
  });

  const [analysisFilters, setAnalysisFilters] = useState({
    symbol: '',
    type: '',
    sort: 'timestamp_desc'
  });

  const [tradeForm, setTradeForm] = useState({
    symbol: '',
    action: 'BUY',
    quantity: 1,
    price: 0,
    status: 'pending'
  });

  const [analysisForm, setAnalysisForm] = useState({
    symbol: '',
    analysis_type: '',
    result: '',
    confidence: 0.5
  });

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, autoRefreshMs);
    return () => clearInterval(interval);
  }, [autoRefreshMs]);

  const fetchData = async () => {
    const started = Date.now();
    try {
      const [healthRes, tradesRes, analysisRes] = await Promise.all([
        axios.get(`${API_URL}/health`),
        axios.get(`${API_URL}/api/trades?limit=100`),
        axios.get(`${API_URL}/api/analysis?limit=100`)
      ]);

      const status = healthRes?.data?.status === 'healthy' ? 'healthy' : 'error';
      setApiStatus(status);
      setTrades(tradesRes.data);
      setAnalysis(analysisRes.data);
      setLastUpdated(new Date().toISOString());
      setResponseTimeMs(Date.now() - started);
      setLoading(false);
    } catch (error) {
      setApiStatus('error');
      setLoading(false);
      setResponseTimeMs(Date.now() - started);
      setErrorLog((prev) => [
        {
          id: `${Date.now()}-${prev.length}`,
          message: error?.message || 'Unexpected error',
          timestamp: new Date().toISOString()
        },
        ...prev
      ].slice(0, 10));
    }
  };

  const createSampleTrade = async () => {
    try {
      const sampleTrade = {
        symbol: 'AAPL',
        action: 'BUY',
        quantity: 10,
        price: 150.5,
        total_value: 1505,
        status: 'completed'
      };
      await axios.post(`${API_URL}/api/trades`, sampleTrade);
      fetchData();
    } catch (error) {
      setErrorLog((prev) => [
        {
          id: `${Date.now()}-${prev.length}`,
          message: error?.message || 'Failed to create trade',
          timestamp: new Date().toISOString()
        },
        ...prev
      ].slice(0, 10));
    }
  };

  const createSampleAnalysis = async () => {
    try {
      const sampleAnalysis = {
        symbol: 'AAPL',
        analysis_type: 'Trend Analysis',
        result: 'Bullish trend detected',
        confidence: 0.85
      };
      await axios.post(`${API_URL}/api/analysis`, sampleAnalysis);
      fetchData();
    } catch (error) {
      setErrorLog((prev) => [
        {
          id: `${Date.now()}-${prev.length}`,
          message: error?.message || 'Failed to create analysis',
          timestamp: new Date().toISOString()
        },
        ...prev
      ].slice(0, 10));
    }
  };

  const handleTradeSubmit = async (event) => {
    event.preventDefault();
    setTradeFormError('');
    const totalValue = Number(tradeForm.quantity) * Number(tradeForm.price);
    if (!tradeForm.symbol || !tradeForm.price || !tradeForm.quantity) {
      setTradeFormError('Symbol, price and quantity are required.');
      return;
    }
    try {
      await axios.post(`${API_URL}/api/trades`, {
        ...tradeForm,
        total_value: totalValue
      });
      setTradeForm({
        symbol: '',
        action: 'BUY',
        quantity: 1,
        price: 0,
        status: 'pending'
      });
      fetchData();
    } catch (error) {
      setTradeFormError(error?.message || 'Failed to submit trade.');
    }
  };

  const handleAnalysisSubmit = async (event) => {
    event.preventDefault();
    setAnalysisFormError('');
    if (!analysisForm.symbol || !analysisForm.analysis_type || !analysisForm.result) {
      setAnalysisFormError('Symbol, analysis type and result are required.');
      return;
    }
    try {
      await axios.post(`${API_URL}/api/analysis`, {
        ...analysisForm,
        confidence: Number(analysisForm.confidence)
      });
      setAnalysisForm({
        symbol: '',
        analysis_type: '',
        result: '',
        confidence: 0.5
      });
      fetchData();
    } catch (error) {
      setAnalysisFormError(error?.message || 'Failed to submit analysis.');
    }
  };

  const handleRiskSave = () => {
    localStorage.setItem('riskSettings', JSON.stringify(riskSettings));
    setAlertLog((prev) => [
      {
        id: `${Date.now()}-${prev.length}`,
        level: 'info',
        message: 'Risk settings saved locally.',
        timestamp: new Date().toISOString()
      },
      ...prev
    ].slice(0, 10));
  };

  const handleTestAlert = () => {
    setAlertLog((prev) => [
      {
        id: `${Date.now()}-${prev.length}`,
        level: 'warning',
        message: 'Test alert triggered from UI.',
        timestamp: new Date().toISOString()
      },
      ...prev
    ].slice(0, 10));
  };

  const totalTradeValue = useMemo(
    () => trades.reduce((sum, trade) => sum + Number(trade.total_value || 0), 0),
    [trades]
  );

  const avgConfidence = useMemo(() => {
    if (!analysis.length) return 0;
    const total = analysis.reduce(
      (sum, item) => sum + Number(item.confidence || 0),
      0
    );
    return total / analysis.length;
  }, [analysis]);

  const filteredTrades = useMemo(() => {
    const filtered = trades.filter((trade) => {
      const matchesSymbol = tradeFilters.symbol
        ? trade.symbol?.toLowerCase().includes(tradeFilters.symbol.toLowerCase())
        : true;
      const matchesAction = tradeFilters.action
        ? trade.action === tradeFilters.action
        : true;
      const matchesStatus = tradeFilters.status
        ? trade.status === tradeFilters.status
        : true;
      return matchesSymbol && matchesAction && matchesStatus;
    });

    const sorted = [...filtered];
    switch (tradeFilters.sort) {
      case 'timestamp_asc':
        sorted.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
        break;
      case 'value_desc':
        sorted.sort((a, b) => (b.total_value || 0) - (a.total_value || 0));
        break;
      case 'value_asc':
        sorted.sort((a, b) => (a.total_value || 0) - (b.total_value || 0));
        break;
      default:
        sorted.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
    }
    return sorted;
  }, [trades, tradeFilters]);

  const filteredAnalysis = useMemo(() => {
    const filtered = analysis.filter((item) => {
      const matchesSymbol = analysisFilters.symbol
        ? item.symbol?.toLowerCase().includes(analysisFilters.symbol.toLowerCase())
        : true;
      const matchesType = analysisFilters.type
        ? item.analysis_type?.toLowerCase().includes(analysisFilters.type.toLowerCase())
        : true;
      return matchesSymbol && matchesType;
    });

    const sorted = [...filtered];
    switch (analysisFilters.sort) {
      case 'confidence_asc':
        sorted.sort((a, b) => (a.confidence || 0) - (b.confidence || 0));
        break;
      case 'confidence_desc':
        sorted.sort((a, b) => (b.confidence || 0) - (a.confidence || 0));
        break;
      case 'timestamp_asc':
        sorted.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
        break;
      default:
        sorted.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
    }
    return sorted;
  }, [analysis, analysisFilters]);

  const tabs = [
    'dashboard',
    'trades',
    'analysis',
    'risk',
    'settings',
    'alerts',
    'diagnostics'
  ];

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div>
            <h1>🤖 RLdC AiNalyzator</h1>
            <p>AI-Powered Trading Analysis & Monitoring</p>
          </div>
          <div className="header-metrics">
            <div className={`status-indicator ${apiStatus}`}>
              API Status: {apiStatus}
            </div>
            <span className="meta">Last update: {formatTimestamp(lastUpdated)}</span>
            <span className="meta">
              Response time: {responseTimeMs ? `${responseTimeMs} ms` : '—'}
            </span>
          </div>
        </div>
      </header>

      <nav className="navigation">
        {tabs.map((tab) => (
          <button
            key={tab}
            className={activeTab === tab ? 'active' : ''}
            onClick={() => setActiveTab(tab)}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </nav>

      <main className="content">
        {loading ? (
          <div className="loading">Loading...</div>
        ) : (
          <>
            {activeTab === 'dashboard' && (
              <div className="dashboard">
                <div className="stats-grid">
                  <div className="stat-card">
                    <h3>Total Trades</h3>
                    <div className="stat-value">{trades.length}</div>
                  </div>
                  <div className="stat-card">
                    <h3>Total Analysis</h3>
                    <div className="stat-value">{analysis.length}</div>
                  </div>
                  <div className="stat-card">
                    <h3>Total Value</h3>
                    <div className="stat-value">${formatNumber(totalTradeValue)}</div>
                  </div>
                  <div className="stat-card">
                    <h3>Avg Confidence</h3>
                    <div className="stat-value">{formatNumber(avgConfidence * 100, 0)}%</div>
                  </div>
                </div>
                <div className="panel-grid">
                  <div className="panel">
                    <h3>Quick Actions</h3>
                    <div className="actions">
                      <button onClick={createSampleTrade} className="action-btn">
                        Add Sample Trade
                      </button>
                      <button onClick={createSampleAnalysis} className="action-btn">
                        Add Sample Analysis
                      </button>
                      <button onClick={fetchData} className="action-btn">
                        Refresh Data
                      </button>
                      <button onClick={handleTestAlert} className="action-btn secondary">
                        Trigger Test Alert
                      </button>
                    </div>
                  </div>
                  <div className="panel">
                    <h3>System Snapshot</h3>
                    <div className="snapshot">
                      <div>
                        <span className="label">API URL</span>
                        <span>{API_URL}</span>
                      </div>
                      <div>
                        <span className="label">Auto refresh</span>
                        <span>{autoRefreshMs / 1000}s</span>
                      </div>
                      <div>
                        <span className="label">Last update</span>
                        <span>{formatTimestamp(lastUpdated)}</span>
                      </div>
                      <div>
                        <span className="label">Errors</span>
                        <span>{errorLog.length}</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'trades' && (
              <div className="trades-section">
                <h2>Trades</h2>
                <div className="filters">
                  <input
                    type="text"
                    placeholder="Filter by symbol"
                    value={tradeFilters.symbol}
                    onChange={(event) =>
                      setTradeFilters((prev) => ({
                        ...prev,
                        symbol: event.target.value
                      }))
                    }
                  />
                  <select
                    value={tradeFilters.action}
                    onChange={(event) =>
                      setTradeFilters((prev) => ({
                        ...prev,
                        action: event.target.value
                      }))
                    }
                  >
                    <option value="">All actions</option>
                    <option value="BUY">BUY</option>
                    <option value="SELL">SELL</option>
                  </select>
                  <select
                    value={tradeFilters.status}
                    onChange={(event) =>
                      setTradeFilters((prev) => ({
                        ...prev,
                        status: event.target.value
                      }))
                    }
                  >
                    <option value="">All statuses</option>
                    <option value="completed">completed</option>
                    <option value="pending">pending</option>
                  </select>
                  <select
                    value={tradeFilters.sort}
                    onChange={(event) =>
                      setTradeFilters((prev) => ({
                        ...prev,
                        sort: event.target.value
                      }))
                    }
                  >
                    <option value="timestamp_desc">Newest first</option>
                    <option value="timestamp_asc">Oldest first</option>
                    <option value="value_desc">Highest value</option>
                    <option value="value_asc">Lowest value</option>
                  </select>
                </div>

                <form className="form" onSubmit={handleTradeSubmit}>
                  <h3>Create Trade</h3>
                  <div className="form-grid">
                    <input
                      type="text"
                      placeholder="Symbol"
                      value={tradeForm.symbol}
                      onChange={(event) =>
                        setTradeForm((prev) => ({
                          ...prev,
                          symbol: event.target.value.toUpperCase()
                        }))
                      }
                    />
                    <select
                      value={tradeForm.action}
                      onChange={(event) =>
                        setTradeForm((prev) => ({
                          ...prev,
                          action: event.target.value
                        }))
                      }
                    >
                      <option value="BUY">BUY</option>
                      <option value="SELL">SELL</option>
                    </select>
                    <input
                      type="number"
                      min="0"
                      step="0.01"
                      placeholder="Quantity"
                      value={tradeForm.quantity}
                      onChange={(event) =>
                        setTradeForm((prev) => ({
                          ...prev,
                          quantity: event.target.value
                        }))
                      }
                    />
                    <input
                      type="number"
                      min="0"
                      step="0.01"
                      placeholder="Price"
                      value={tradeForm.price}
                      onChange={(event) =>
                        setTradeForm((prev) => ({
                          ...prev,
                          price: event.target.value
                        }))
                      }
                    />
                    <select
                      value={tradeForm.status}
                      onChange={(event) =>
                        setTradeForm((prev) => ({
                          ...prev,
                          status: event.target.value
                        }))
                      }
                    >
                      <option value="pending">pending</option>
                      <option value="completed">completed</option>
                    </select>
                    <div className="form-note">
                      Total value: ${formatNumber(
                        Number(tradeForm.quantity) * Number(tradeForm.price)
                      )}
                    </div>
                  </div>
                  {tradeFormError && <p className="form-error">{tradeFormError}</p>}
                  <button type="submit" className="action-btn">
                    Submit Trade
                  </button>
                </form>

                {filteredTrades.length === 0 ? (
                  <p>No trades found. Add one to populate the table.</p>
                ) : (
                  <div className="table-container">
                    <table>
                      <thead>
                        <tr>
                          <th>Time</th>
                          <th>Symbol</th>
                          <th>Action</th>
                          <th>Quantity</th>
                          <th>Price</th>
                          <th>Total Value</th>
                          <th>Status</th>
                        </tr>
                      </thead>
                      <tbody>
                        {filteredTrades.map((trade) => (
                          <tr key={trade.id}>
                            <td>{formatTimestamp(trade.timestamp)}</td>
                            <td>{trade.symbol}</td>
                            <td className={`action ${trade.action?.toLowerCase()}`}>
                              {trade.action}
                            </td>
                            <td>{trade.quantity}</td>
                            <td>${formatNumber(trade.price)}</td>
                            <td>${formatNumber(trade.total_value)}</td>
                            <td className={`status ${trade.status}`}>{trade.status}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'analysis' && (
              <div className="analysis-section">
                <h2>Analysis</h2>
                <div className="filters">
                  <input
                    type="text"
                    placeholder="Filter by symbol"
                    value={analysisFilters.symbol}
                    onChange={(event) =>
                      setAnalysisFilters((prev) => ({
                        ...prev,
                        symbol: event.target.value
                      }))
                    }
                  />
                  <input
                    type="text"
                    placeholder="Filter by type"
                    value={analysisFilters.type}
                    onChange={(event) =>
                      setAnalysisFilters((prev) => ({
                        ...prev,
                        type: event.target.value
                      }))
                    }
                  />
                  <select
                    value={analysisFilters.sort}
                    onChange={(event) =>
                      setAnalysisFilters((prev) => ({
                        ...prev,
                        sort: event.target.value
                      }))
                    }
                  >
                    <option value="timestamp_desc">Newest first</option>
                    <option value="timestamp_asc">Oldest first</option>
                    <option value="confidence_desc">Highest confidence</option>
                    <option value="confidence_asc">Lowest confidence</option>
                  </select>
                </div>

                <form className="form" onSubmit={handleAnalysisSubmit}>
                  <h3>Create Analysis</h3>
                  <div className="form-grid">
                    <input
                      type="text"
                      placeholder="Symbol"
                      value={analysisForm.symbol}
                      onChange={(event) =>
                        setAnalysisForm((prev) => ({
                          ...prev,
                          symbol: event.target.value.toUpperCase()
                        }))
                      }
                    />
                    <input
                      type="text"
                      placeholder="Analysis type"
                      value={analysisForm.analysis_type}
                      onChange={(event) =>
                        setAnalysisForm((prev) => ({
                          ...prev,
                          analysis_type: event.target.value
                        }))
                      }
                    />
                    <textarea
                      placeholder="Analysis result"
                      value={analysisForm.result}
                      onChange={(event) =>
                        setAnalysisForm((prev) => ({
                          ...prev,
                          result: event.target.value
                        }))
                      }
                    />
                    <input
                      type="number"
                      min="0"
                      max="1"
                      step="0.01"
                      placeholder="Confidence (0-1)"
                      value={analysisForm.confidence}
                      onChange={(event) =>
                        setAnalysisForm((prev) => ({
                          ...prev,
                          confidence: event.target.value
                        }))
                      }
                    />
                  </div>
                  {analysisFormError && (
                    <p className="form-error">{analysisFormError}</p>
                  )}
                  <button type="submit" className="action-btn">
                    Submit Analysis
                  </button>
                </form>

                {filteredAnalysis.length === 0 ? (
                  <p>No analysis found. Add one to populate the grid.</p>
                ) : (
                  <div className="analysis-grid">
                    {filteredAnalysis.map((item) => (
                      <div key={item.id} className="analysis-card">
                        <div className="analysis-header">
                          <span className="symbol">{item.symbol}</span>
                          <span className="type">{item.analysis_type}</span>
                        </div>
                        <div className="analysis-result">{item.result}</div>
                        <div className="confidence">
                          Confidence: {formatNumber(item.confidence * 100, 0)}%
                        </div>
                        <div className="timestamp">{formatTimestamp(item.timestamp)}</div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {activeTab === 'risk' && (
              <div className="panel full">
                <h2>Risk Management</h2>
                <p className="subtitle">Configure local risk limits for strategy validation.</p>
                <div className="form-grid">
                  <input
                    type="number"
                    min="0"
                    step="1"
                    placeholder="Max daily loss (USD)"
                    value={riskSettings.maxDailyLoss}
                    onChange={(event) =>
                      setRiskSettings((prev) => ({
                        ...prev,
                        maxDailyLoss: Number(event.target.value)
                      }))
                    }
                  />
                  <input
                    type="number"
                    min="0"
                    step="1"
                    placeholder="Max exposure (USD)"
                    value={riskSettings.maxExposure}
                    onChange={(event) =>
                      setRiskSettings((prev) => ({
                        ...prev,
                        maxExposure: Number(event.target.value)
                      }))
                    }
                  />
                  <input
                    type="number"
                    min="0"
                    step="0.1"
                    placeholder="Stop loss (%)"
                    value={riskSettings.stopLossPct}
                    onChange={(event) =>
                      setRiskSettings((prev) => ({
                        ...prev,
                        stopLossPct: Number(event.target.value)
                      }))
                    }
                  />
                  <input
                    type="number"
                    min="0"
                    step="0.1"
                    placeholder="Take profit (%)"
                    value={riskSettings.takeProfitPct}
                    onChange={(event) =>
                      setRiskSettings((prev) => ({
                        ...prev,
                        takeProfitPct: Number(event.target.value)
                      }))
                    }
                  />
                </div>
                <button type="button" className="action-btn" onClick={handleRiskSave}>
                  Save Risk Settings
                </button>
              </div>
            )}

            {activeTab === 'settings' && (
              <div className="panel full">
                <h2>Settings</h2>
                <div className="settings-grid">
                  <div>
                    <h4>API Configuration</h4>
                    <p>API URL: {API_URL}</p>
                    <p>Ensure CORS allows your frontend host.</p>
                  </div>
                  <div>
                    <h4>Auto refresh</h4>
                    <select
                      value={autoRefreshMs}
                      onChange={(event) => setAutoRefreshMs(Number(event.target.value))}
                    >
                      {REFRESH_OPTIONS.map((value) => (
                        <option key={value} value={value}>
                          {value / 1000} seconds
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'alerts' && (
              <div className="panel full">
                <h2>Alerts</h2>
                <button type="button" className="action-btn" onClick={handleTestAlert}>
                  Trigger Test Alert
                </button>
                <div className="list">
                  {alertLog.length === 0 ? (
                    <p>No alerts generated yet.</p>
                  ) : (
                    alertLog.map((alert) => (
                      <div key={alert.id} className={`list-item ${alert.level}`}>
                        <div>
                          <strong>{alert.level.toUpperCase()}</strong> — {alert.message}
                        </div>
                        <span>{formatTimestamp(alert.timestamp)}</span>
                      </div>
                    ))
                  )}
                </div>
              </div>
            )}

            {activeTab === 'diagnostics' && (
              <div className="panel full">
                <h2>Diagnostics</h2>
                <div className="snapshot">
                  <div>
                    <span className="label">API status</span>
                    <span>{apiStatus}</span>
                  </div>
                  <div>
                    <span className="label">Last update</span>
                    <span>{formatTimestamp(lastUpdated)}</span>
                  </div>
                  <div>
                    <span className="label">Response time</span>
                    <span>{responseTimeMs ? `${responseTimeMs} ms` : '—'}</span>
                  </div>
                </div>
                <h3>Error Log</h3>
                <div className="list">
                  {errorLog.length === 0 ? (
                    <p>No errors recorded.</p>
                  ) : (
                    errorLog.map((item) => (
                      <div key={item.id} className="list-item error">
                        <div>{item.message}</div>
                        <span>{formatTimestamp(item.timestamp)}</span>
                      </div>
                    ))
                  )}
                </div>
              </div>
            )}
          </>
        )}
      </main>

      <footer className="App-footer">
        <p>© 2024 RLdC AiNalyzator - Trading Analysis System</p>
      </footer>
    </div>
  );
}

export default App;
