import React, { useEffect, useMemo, useState } from 'react';
import axios from 'axios';
import './App.css';

const DEFAULT_API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';
const DEFAULT_REFRESH_SECONDS = 30;

const formatCurrency = (value) =>
  new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    maximumFractionDigits: 2,
  }).format(value || 0);

const formatNumber = (value, digits = 2) => Number(value || 0).toFixed(digits);

function App() {
    const [trades, setTrades] = useState([]);
    const [analysis, setAnalysis] = useState([]);
    const [loading, setLoading] = useState(true);
    const [activeTab, setActiveTab] = useState('dashboard');
    const [apiStatus, setApiStatus] = useState('unknown');
    const [lastUpdated, setLastUpdated] = useState(null);
    const [responseTimeMs, setResponseTimeMs] = useState(null);
    const [errorLog, setErrorLog] = useState([]);
    const [autoRefresh, setAutoRefresh] = useState(true);
    const [refreshSeconds, setRefreshSeconds] = useState(DEFAULT_REFRESH_SECONDS);
    const [apiBaseUrl, setApiBaseUrl] = useState(DEFAULT_API_URL);
    const [pendingApiBaseUrl, setPendingApiBaseUrl] = useState(DEFAULT_API_URL);

    const [tradeFilters, setTradeFilters] = useState({
      symbol: '',
      action: 'ALL',
      status: 'ALL',
      search: '',
    });
    const [analysisFilters, setAnalysisFilters] = useState({
      symbol: '',
      type: 'ALL',
    });

    const [tradeForm, setTradeForm] = useState({
      symbol: '',
      action: 'BUY',
      quantity: '1',
      price: '100',
      status: 'pending',
    });

    const [analysisForm, setAnalysisForm] = useState({
      symbol: '',
      analysis_type: 'Trend Analysis',
      result: '',
      confidence: '0.7',
    });

    const [riskSettings, setRiskSettings] = useState({
      maxDailyLoss: '500',
      maxExposure: '5000',
      maxPositions: '5',
      stopLossPct: '2',
      takeProfitPct: '4',
    });

    const [alerts, setAlerts] = useState([
      { id: 1, level: 'info', message: 'System monitor initialized.' },
    ]);
    const [newAlert, setNewAlert] = useState({
      level: 'info',
      message: '',
    });

    const fetchData = async () => {
      try {
        const start = performance.now();
        const [healthRes, tradesRes, analysisRes] = await Promise.all([
          axios.get(`${apiBaseUrl}/health`),
          axios.get(`${apiBaseUrl}/api/trades?limit=100`),
          axios.get(`${apiBaseUrl}/api/analysis?limit=100`),
        ]);

        const end = performance.now();
        setResponseTimeMs(Math.round(end - start));
        setApiStatus(healthRes.data?.status === 'healthy' ? 'healthy' : 'unknown');
        setTrades(tradesRes.data || []);
        setAnalysis(analysisRes.data || []);
        setLastUpdated(new Date());
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setApiStatus('error');
        setLoading(false);
        setErrorLog((prev) => [
          {
            id: Date.now(),
            time: new Date().toLocaleString(),
            message: error?.message || 'Unknown API error',
          },
          ...prev,
        ].slice(0, 5));
      }
    };

    useEffect(() => {
      fetchData();
    }, [apiBaseUrl]);

    useEffect(() => {
      if (!autoRefresh) return undefined;
      const interval = setInterval(fetchData, refreshSeconds * 1000);
      return () => clearInterval(interval);
    }, [autoRefresh, refreshSeconds, apiBaseUrl]);

    const createTrade = async (payload) => {
      try {
        await axios.post(`${apiBaseUrl}/api/trades`, payload);
        fetchData();
      } catch (error) {
        console.error('Error creating trade:', error);
        setErrorLog((prev) => [
          {
            id: Date.now(),
            time: new Date().toLocaleString(),
            message: error?.message || 'Failed to create trade',
          },
          ...prev,
        ].slice(0, 5));
      }
    };

    const createAnalysis = async (payload) => {
      try {
        await axios.post(`${apiBaseUrl}/api/analysis`, payload);
        fetchData();
      } catch (error) {
        console.error('Error creating analysis:', error);
        setErrorLog((prev) => [
          {
            id: Date.now(),
            time: new Date().toLocaleString(),
            message: error?.message || 'Failed to create analysis',
          },
          ...prev,
        ].slice(0, 5));
      }
    };

    const createSampleTrade = async () => {
      await createTrade({
        symbol: 'AAPL',
        action: 'BUY',
        quantity: 10,
        price: 150.5,
        total_value: 1505,
        status: 'completed',
      });
    };

    const createSampleAnalysis = async () => {
      await createAnalysis({
        symbol: 'AAPL',
        analysis_type: 'Trend Analysis',
        result: 'Bullish trend detected',
        confidence: 0.85,
      });
    };

    const filteredTrades = useMemo(() => {
      return trades
        .filter((trade) =>
          tradeFilters.symbol
            ? trade.symbol.toLowerCase().includes(tradeFilters.symbol.toLowerCase())
            : true
        )
        .filter((trade) =>
          tradeFilters.action === 'ALL'
            ? true
            : trade.action === tradeFilters.action
        )
        .filter((trade) =>
          tradeFilters.status === 'ALL'
            ? true
            : trade.status === tradeFilters.status
        )
        .filter((trade) =>
          tradeFilters.search
            ? trade.symbol.toLowerCase().includes(tradeFilters.search.toLowerCase())
            : true
        );
    }, [trades, tradeFilters]);

    const filteredAnalysis = useMemo(() => {
      return analysis
        .filter((item) =>
          analysisFilters.symbol
            ? item.symbol.toLowerCase().includes(analysisFilters.symbol.toLowerCase())
            : true
        )
        .filter((item) =>
          analysisFilters.type === 'ALL'
            ? true
            : item.analysis_type === analysisFilters.type
        );
    }, [analysis, analysisFilters]);

    const totalTradeValue = useMemo(() =>
      trades.reduce((sum, trade) => sum + Number(trade.total_value || 0), 0), [trades]);

    const avgConfidence = useMemo(() => {
      if (!analysis.length) return 0;
      const total = analysis.reduce((sum, item) => sum + Number(item.confidence || 0), 0);
      return total / analysis.length;
    }, [analysis]);

    const applySettings = () => {
      setApiBaseUrl(pendingApiBaseUrl || DEFAULT_API_URL);
    };

    const handleTradeSubmit = (event) => {
      event.preventDefault();
      const quantity = Number(tradeForm.quantity);
      const price = Number(tradeForm.price);
      if (!tradeForm.symbol || !quantity || !price) return;
      createTrade({
        symbol: tradeForm.symbol.toUpperCase(),
        action: tradeForm.action,
        quantity,
        price,
        total_value: quantity * price,
        status: tradeForm.status,
      });
    };

    const handleAnalysisSubmit = (event) => {
      event.preventDefault();
      if (!analysisForm.symbol || !analysisForm.result) return;
      createAnalysis({
        symbol: analysisForm.symbol.toUpperCase(),
        analysis_type: analysisForm.analysis_type,
        result: analysisForm.result,
        confidence: Number(analysisForm.confidence || 0),
      });
    };

    const addAlert = (event) => {
      event.preventDefault();
      if (!newAlert.message.trim()) return;
      setAlerts((prev) => [
        { id: Date.now(), ...newAlert },
        ...prev,
      ]);
      setNewAlert({ level: 'info', message: '' });
    };

    return (
      <div className="App">
        <header className="App-header">
          <div className="header-top">
            <div>
              <h1>🤖 RLdC AiNalyzator</h1>
              <p>AI-Powered Trading Analysis & Monitoring</p>
            </div>
            <div className="status-pill">
              <span className={`status-dot ${apiStatus}`} />
              <span>{apiStatus}</span>
            </div>
          </div>
          <div className="header-meta">
            <span>API: {apiBaseUrl}</span>
            <span>Last update: {lastUpdated ? lastUpdated.toLocaleString() : '—'}</span>
            <span>Response time: {responseTimeMs ? `${responseTimeMs} ms` : '—'}</span>
          </div>
        </header>

        <nav className="navigation">
          {[
            { id: 'dashboard', label: 'Dashboard' },
            { id: 'trades', label: 'Trades' },
            { id: 'analysis', label: 'Analysis' },
            { id: 'risk', label: 'Risk' },
            { id: 'alerts', label: 'Alerts' },
            { id: 'settings', label: 'Settings' },
            { id: 'diagnostics', label: 'Diagnostics' },
          ].map((tab) => (
            <button
              key={tab.id}
              className={activeTab === tab.id ? 'active' : ''}
              onClick={() => setActiveTab(tab.id)}
            >
              {tab.label}
            </button>
          ))}
        </nav>

        <main className="content">
          {loading ? (
            <div className="loading">Loading...</div>
          ) : (
            <>
              {activeTab === 'dashboard' && (
                <div className="dashboard">
                  <div className="stats-grid">
                    <div className="stat-card">
                      <h3>Total Trades</h3>
                      <div className="stat-value">{trades.length}</div>
                      <div className="stat-meta">Value: {formatCurrency(totalTradeValue)}</div>
                    </div>
                    <div className="stat-card">
                      <h3>Total Analysis</h3>
                      <div className="stat-value">{analysis.length}</div>
                      <div className="stat-meta">Avg. confidence: {formatNumber(avgConfidence * 100, 0)}%</div>
                    </div>
                    <div className="stat-card">
                      <h3>System Status</h3>
                      <div className="stat-value">{apiStatus}</div>
                      <div className="stat-meta">Auto refresh: {autoRefresh ? 'On' : 'Off'}</div>
                    </div>
                    <div className="stat-card">
                      <h3>Performance</h3>
                      <div className="stat-value">{responseTimeMs ? `${responseTimeMs} ms` : '—'}</div>
                      <div className="stat-meta">Last update: {lastUpdated ? lastUpdated.toLocaleTimeString() : '—'}</div>
                    </div>
                  </div>
                  <div className="actions">
                    <button onClick={createSampleTrade} className="action-btn">
                      Add Sample Trade
                    </button>
                    <button onClick={createSampleAnalysis} className="action-btn">
                      Add Sample Analysis
                    </button>
                    <button onClick={fetchData} className="action-btn ghost">
                      Refresh Data
                    </button>
                  </div>
                </div>
              )}

              {activeTab === 'trades' && (
                <div className="section">
                  <div className="section-header">
                    <h2>Trades</h2>
                    <span className="badge">{filteredTrades.length} results</span>
                  </div>

                  <div className="filters">
                    <input
                      className="input"
                      placeholder="Symbol"
                      value={tradeFilters.symbol}
                      onChange={(e) => setTradeFilters({ ...tradeFilters, symbol: e.target.value })}
                    />
                    <select
                      className="input"
                      value={tradeFilters.action}
                      onChange={(e) => setTradeFilters({ ...tradeFilters, action: e.target.value })}
                    >
                      <option value="ALL">All actions</option>
                      <option value="BUY">BUY</option>
                      <option value="SELL">SELL</option>
                    </select>
                    <select
                      className="input"
                      value={tradeFilters.status}
                      onChange={(e) => setTradeFilters({ ...tradeFilters, status: e.target.value })}
                    >
                      <option value="ALL">All status</option>
                      <option value="pending">pending</option>
                      <option value="completed">completed</option>
                    </select>
                    <input
                      className="input"
                      placeholder="Search"
                      value={tradeFilters.search}
                      onChange={(e) => setTradeFilters({ ...tradeFilters, search: e.target.value })}
                    />
                  </div>

                  <form className="form-grid" onSubmit={handleTradeSubmit}>
                    <div>
                      <label>Symbol</label>
                      <input
                        className="input"
                        value={tradeForm.symbol}
                        onChange={(e) => setTradeForm({ ...tradeForm, symbol: e.target.value })}
                        placeholder="BTCUSDT"
                        required
                      />
                    </div>
                    <div>
                      <label>Action</label>
                      <select
                        className="input"
                        value={tradeForm.action}
                        onChange={(e) => setTradeForm({ ...tradeForm, action: e.target.value })}
                      >
                        <option value="BUY">BUY</option>
                        <option value="SELL">SELL</option>
                      </select>
                    </div>
                    <div>
                      <label>Quantity</label>
                      <input
                        className="input"
                        type="number"
                        step="0.01"
                        value={tradeForm.quantity}
                        onChange={(e) => setTradeForm({ ...tradeForm, quantity: e.target.value })}
                        required
                      />
                    </div>
                    <div>
                      <label>Price</label>
                      <input
                        className="input"
                        type="number"
                        step="0.01"
                        value={tradeForm.price}
                        onChange={(e) => setTradeForm({ ...tradeForm, price: e.target.value })}
                        required
                      />
                    </div>
                    <div>
                      <label>Status</label>
                      <select
                        className="input"
                        value={tradeForm.status}
                        onChange={(e) => setTradeForm({ ...tradeForm, status: e.target.value })}
                      >
                        <option value="pending">pending</option>
                        <option value="completed">completed</option>
                      </select>
                    </div>
                    <div className="form-actions">
                      <button type="submit" className="action-btn">
                        Create Trade
                      </button>
                      <div className="muted">
                        Total: {formatCurrency(Number(tradeForm.quantity) * Number(tradeForm.price))}
                      </div>
                    </div>
                  </form>

                  {filteredTrades.length === 0 ? (
                    <p className="muted">No trades found.</p>
                  ) : (
                    <div className="table-container">
                      <table>
                        <thead>
                          <tr>
                            <th>Time</th>
                            <th>Symbol</th>
                            <th>Action</th>
                            <th>Quantity</th>
                            <th>Price</th>
                            <th>Total Value</th>
                            <th>Status</th>
                          </tr>
                        </thead>
                        <tbody>
                          {filteredTrades.map((trade) => (
                            <tr key={trade.id}>
                              <td>{new Date(trade.timestamp).toLocaleString()}</td>
                              <td>{trade.symbol}</td>
                              <td className={`action ${trade.action.toLowerCase()}`}>
                                {trade.action}
                              </td>
                              <td>{trade.quantity}</td>
                              <td>{formatCurrency(trade.price)}</td>
                              <td>{formatCurrency(trade.total_value)}</td>
                              <td className={`status ${trade.status}`}>{trade.status}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  )}
                </div>
              )}

              {activeTab === 'analysis' && (
                <div className="section">
                  <div className="section-header">
                    <h2>Analysis</h2>
                    <span className="badge">{filteredAnalysis.length} results</span>
                  </div>

                  <div className="filters">
                    <input
                      className="input"
                      placeholder="Symbol"
                      value={analysisFilters.symbol}
                      onChange={(e) => setAnalysisFilters({ ...analysisFilters, symbol: e.target.value })}
                    />
                    <select
                      className="input"
                      value={analysisFilters.type}
                      onChange={(e) => setAnalysisFilters({ ...analysisFilters, type: e.target.value })}
                    >
                      <option value="ALL">All types</option>
                      {[...new Set(analysis.map((item) => item.analysis_type))].map((type) => (
                        <option key={type} value={type}>{type}</option>
                      ))}
                    </select>
                  </div>

                  <form className="form-grid" onSubmit={handleAnalysisSubmit}>
                    <div>
                      <label>Symbol</label>
                      <input
                        className="input"
                        value={analysisForm.symbol}
                        onChange={(e) => setAnalysisForm({ ...analysisForm, symbol: e.target.value })}
                        placeholder="BTCUSDT"
                        required
                      />
                    </div>
                    <div>
                      <label>Type</label>
                      <input
                        className="input"
                        value={analysisForm.analysis_type}
                        onChange={(e) => setAnalysisForm({ ...analysisForm, analysis_type: e.target.value })}
                        required
                      />
                    </div>
                    <div>
                      <label>Confidence</label>
                      <input
                        className="input"
                        type="number"
                        step="0.01"
                        min="0"
                        max="1"
                        value={analysisForm.confidence}
                        onChange={(e) => setAnalysisForm({ ...analysisForm, confidence: e.target.value })}
                      />
                    </div>
                    <div className="form-span">
                      <label>Result</label>
                      <input
                        className="input"
                        value={analysisForm.result}
                        onChange={(e) => setAnalysisForm({ ...analysisForm, result: e.target.value })}
                        placeholder="Bullish trend detected"
                        required
                      />
                    </div>
                    <div className="form-actions">
                      <button type="submit" className="action-btn">
                        Create Analysis
                      </button>
                      <div className="muted">
                        Confidence: {formatNumber(Number(analysisForm.confidence) * 100, 0)}%
                      </div>
                    </div>
                  </form>

                  {filteredAnalysis.length === 0 ? (
                    <p className="muted">No analysis found.</p>
                  ) : (
                    <div className="analysis-grid">
                      {filteredAnalysis.map((item) => (
                        <div key={item.id} className="analysis-card">
                          <div className="analysis-header">
                            <span className="symbol">{item.symbol}</span>
                            <span className="type">{item.analysis_type}</span>
                          </div>
                          <div className="analysis-result">{item.result}</div>
                          {item.confidence !== null && item.confidence !== undefined && (
                            <div className="confidence">
                              Confidence: {formatNumber(item.confidence * 100, 0)}%
                            </div>
                          )}
                          <div className="timestamp">
                            {new Date(item.timestamp).toLocaleString()}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              )}

              {activeTab === 'risk' && (
                <div className="section">
                  <div className="section-header">
                    <h2>Risk Management</h2>
                    <span className="badge">Local profile</span>
                  </div>
                  <div className="form-grid">
                    <div>
                      <label>Max daily loss (USD)</label>
                      <input
                        className="input"
                        value={riskSettings.maxDailyLoss}
                        onChange={(e) => setRiskSettings({ ...riskSettings, maxDailyLoss: e.target.value })}
                      />
                    </div>
                    <div>
                      <label>Max exposure (USD)</label>
                      <input
                        className="input"
                        value={riskSettings.maxExposure}
                        onChange={(e) => setRiskSettings({ ...riskSettings, maxExposure: e.target.value })}
                      />
                    </div>
                    <div>
                      <label>Max positions</label>
                      <input
                        className="input"
                        value={riskSettings.maxPositions}
                        onChange={(e) => setRiskSettings({ ...riskSettings, maxPositions: e.target.value })}
                      />
                    </div>
                    <div>
                      <label>Stop loss (%)</label>
                      <input
                        className="input"
                        value={riskSettings.stopLossPct}
                        onChange={(e) => setRiskSettings({ ...riskSettings, stopLossPct: e.target.value })}
                      />
                    </div>
                    <div>
                      <label>Take profit (%)</label>
                      <input
                        className="input"
                        value={riskSettings.takeProfitPct}
                        onChange={(e) => setRiskSettings({ ...riskSettings, takeProfitPct: e.target.value })}
                      />
                    </div>
                  </div>
                  <div className="muted">
                    Risk settings are local UI controls until backend endpoints are added.
                  </div>
                </div>
              )}

              {activeTab === 'alerts' && (
                <div className="section">
                  <div className="section-header">
                    <h2>Alerts & Notifications</h2>
                    <span className="badge">{alerts.length} entries</span>
                  </div>
                  <form className="form-grid" onSubmit={addAlert}>
                    <div>
                      <label>Level</label>
                      <select
                        className="input"
                        value={newAlert.level}
                        onChange={(e) => setNewAlert({ ...newAlert, level: e.target.value })}
                      >
                        <option value="info">Info</option>
                        <option value="warning">Warning</option>
                        <option value="critical">Critical</option>
                      </select>
                    </div>
                    <div className="form-span">
                      <label>Message</label>
                      <input
                        className="input"
                        value={newAlert.message}
                        onChange={(e) => setNewAlert({ ...newAlert, message: e.target.value })}
                        placeholder="e.g. Risk limit reached"
                      />
                    </div>
                    <div className="form-actions">
                      <button type="submit" className="action-btn">
                        Add alert
                      </button>
                    </div>
                  </form>

                  <div className="log-list">
                    {alerts.map((alert) => (
                      <div key={alert.id} className={`log-item ${alert.level}`}>
                        <span className="badge">{alert.level}</span>
                        <span>{alert.message}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {activeTab === 'settings' && (
                <div className="section">
                  <div className="section-header">
                    <h2>Settings</h2>
                    <span className="badge">Environment</span>
                  </div>
                  <div className="form-grid">
                    <div className="form-span">
                      <label>API Base URL</label>
                      <input
                        className="input"
                        value={pendingApiBaseUrl}
                        onChange={(e) => setPendingApiBaseUrl(e.target.value)}
                      />
                    </div>
                    <div>
                      <label>Auto refresh</label>
                      <select
                        className="input"
                        value={autoRefresh ? 'on' : 'off'}
                        onChange={(e) => setAutoRefresh(e.target.value === 'on')}
                      >
                        <option value="on">On</option>
                        <option value="off">Off</option>
                      </select>
                    </div>
                    <div>
                      <label>Refresh interval (s)</label>
                      <input
                        className="input"
                        type="number"
                        min="5"
                        value={refreshSeconds}
                        onChange={(e) => setRefreshSeconds(Number(e.target.value))}
                      />
                    </div>
                    <div className="form-actions">
                      <button type="button" className="action-btn" onClick={applySettings}>
                        Apply settings
                      </button>
                      <button type="button" className="action-btn ghost" onClick={fetchData}>
                        Test connection
                      </button>
                    </div>
                  </div>
                  <div className="muted">
                    Settings only affect the current session. Configure REACT_APP_API_URL for persistent changes.
                  </div>
                </div>
              )}

              {activeTab === 'diagnostics' && (
                <div className="section">
                  <div className="section-header">
                    <h2>Diagnostics</h2>
                    <span className="badge">Last 5 events</span>
                  </div>
                  {errorLog.length === 0 ? (
                    <p className="muted">No recent errors.</p>
                  ) : (
                    <div className="log-list">
                      {errorLog.map((entry) => (
                        <div key={entry.id} className="log-item warning">
                          <span className="badge">Error</span>
                          <div>
                            <div>{entry.message}</div>
                            <div className="muted">{entry.time}</div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                  <div className="muted">
                    API status: {apiStatus}. Last update: {lastUpdated ? lastUpdated.toLocaleString() : '—'}.
                  </div>
                </div>
              )}
            </>
          )}
        </main>

        <footer className="App-footer">
          <p>© 2026 RLdC AiNalyzator - Trading Analysis System</p>
        </footer>
      </div>
    );
  }

export default App;
